import React from "react"

class PostPage extends React.Component {

  state = {
    post: [],
    user: []
  }

  componentDidMount() {
    const id = this.props.match.params.id
    fetch(`http://localhost:3000/posts/${id}`)
    .then(resp => resp.json())
    .then(post => {
      this.setState({
        post:post ,
        user:post.user
      })
    })
  }

