import React from "react"
import CommentsContainer from "./CommentsContainer"
import CommentForm from "./CommentForm"
import { Link } from "react-router-dom"

class PostPage extends React.Component {

  state = {
    post: [],
    featured_image: [],
    user: [],
    category:[],
    comments:[],
    likes:[],
    postUser:[]
  }

  componentDidMount() {
    const id = this.props.match.params.id
    fetch(`http://localhost:3000/posts/${id}`)
    .then(resp => resp.json())
    .then(post => {
      this.setState({
        post:post ,
        featured_image: post.featured_image,
        user:post.user,
        category:post.category,
        comments:post.comments,
        likes:post.likes,
        postUser:post.user
      })
      // debugger
    })
  }

  addComment = (comment) => {
    let arrCom = [comment, ...this.state.comments]
    this.setState({
      comments: arrCom
    })
  }

  removeComment = (obj) =>{
    let newComments = this.state.comments.filter(comment => comment.id !==obj.id)

    this.setState((prevState) => ({
      comments:newComments
    }))

    fetch(`http://localhost:3000/comments/${obj.id}`, {
      method:"DELETE"
    })
    .then(resp => resp.json())
    .then((obj) => {
      let newComments = this.state.comments.filter(comment => comment.id !==obj.id)
      this.setState((prevState) => ({
        comments:newComments
      }))
    })
  }



  render(){
    return(
      <div className="postPageContainer">
      <div className="mainContainer">

        <div className="postPageImageContainer">
        <a href={this.state.featured_image.url} target="_blank" rel="noopener noreferrer">
          <img className="postPageImage" src={`${this.state.featured_image.url}`} alt={`${this.state.post.title}`}/>
          </a>
        </div>

        <div className="stuffContainer">
          <div className="stuff">
          stuff
          </div>
          <div className="morestuff">
          more stuff
          </div>
        </div>

        <div className="postAuthorInfoContainer">
          <div className="postAuthorImage">
            <img className="authorImage" src={`${this.state.postUser.image}`} alt="profile" />
          </div>
          <div className="postAuthorContainedInfo">
            <div className="postName">
              {this.state.post.title}
            </div>
            <div className="postAuthorName">
              Created by: <span className="authorName">{this.state.postUser.username}</span>
            </div>
          </div>
        </div>

        <div className="postDescription">
        {this.state.post.description}
        </div>

        <div>
          <CommentForm postId={this.props.match.params.id} updateComment={this.addComment} />
          <CommentsContainer comments={this.state.comments} removeComment={this.removeComment} />
        </div>

      </div>

      <div className="sideContainer">
        <div className="moreByCreater">
          <img className="extraImages" src="http://localhost:3000/rails/active_--b9ee44ed88a73596dafe6ff6af0a646d904736db/AN_12.jpg?content_type=image%2Fjpeg&disposition=inline%3B+filename%3D%22AN_12.jpg%22%3B+filename%2A%3DUTF-8%27%27AN_12.jpg" alt="something" />
          <img className="extraImages" src="http://localhost:3000/rails/active_--167acf0eb41e507c4b61969de4138acd87294d91/AN_13.jpg?content_type=image%2Fjpeg&disposition=inline%3B+filename%3D%22AN_13.jpg%22%3B+filename%2A%3DUTF-8%27%27AN_13.jpg" alt="something" />
          <img className="extraImages" src="http://localhost:3000/rails/active_--8a21fcfa7b24aac9b23d418cd8d3983680afbd85/AN_11.jpg?content_type=image%2Fjpeg&disposition=inline%3B+filename%3D%22AN_11.jpg%22%3B+filename%2A%3DUTF-8%27%27AN_11.jpg" alt="something" />
          <img className="extraImages" src="http://localhost:3000/rails/active_--1c6b3b0f71b79f2005d602e04b3cf3c5ba2ea237/PS_3.jpg?content_type=image%2Fjpeg&disposition=inline%3B+filename%3D%22PS_3.jpg%22%3B+filename%2A%3DUTF-8%27%27PS_3.jpg" alt="something" />
        </div>
      </div>

      </div>
    )
  }
}
export default PostPage
